package project.jun.was.result.exception;import java.util.ArrayList;import project.jun.resource.HoResource;/** * @author  sks */public class HoException  extends Exception {	/**	 *	 */	private static final long serialVersionUID = -7506612931405400257L;	/**	 * @uml.property  name="errMessages"	 */	private ArrayList errMessages;	/**	 * @uml.property  name="javascript"	 */	private String javascript;	private String detailMessage;	/**	 * @uml.property  name="wr"	 * @uml.associationEnd	 */	private HoResource wr = null;	public HoException() {		super( );		this.wr = new HoResource("ERR-CO-9999");		this.errMessages = new ArrayList();	}	public HoException(Exception e) {		super( );		this.wr = new HoResource("ERR-CO-9999");		this.errMessages = new ArrayList();		this.javascript = "";		setDetailErrMessage(e);	}	public HoException(String code) {		super( );		this.wr = new HoResource(code);		this.errMessages = new ArrayList();	}	public HoException( String code, ArrayList errMessages )	{		this( code );		this.errMessages.addAll(errMessages);	}	public HoException( String code, String javascript )	{		this( code );		this.errMessages = new ArrayList();		this.javascript = javascript;	}	public HoException( String code, Exception e )	{		this( code, "", "");		setDetailErrMessage(e);	}	public HoException( String code, String errMessage, String javascript )	{		this( code);		this.errMessages.add(errMessage);		this.javascript = javascript;	}	public HoException( String code, ArrayList errMessages, String javascript )	{		this( code, errMessages );		this.javascript = javascript;	}	public HoException( String code, String errMessage, String javascript, Exception e )	{		this( code, errMessage, javascript);		setDetailErrMessage(e);	}	public HoException( String code, ArrayList errMessages, String javascript, Exception e )	{		this( code, errMessages , javascript);		setDetailErrMessage(e);	}	public HoException(HoResource wr) {		super( );		this.wr = wr;		this.errMessages = new ArrayList();	}	public HoException( HoResource wr, Exception e )	{		this.wr = wr;		setDetailErrMessage(e);	}	public HoException( HoResource wr, ArrayList errMessages )	{		this( wr );		this.errMessages.addAll(errMessages);	}	public HoException( HoResource wr, String javascript )	{		this( wr );		this.javascript = javascript;		this.errMessages = new ArrayList();	}	public HoException( HoResource wr, String errMessage, String javascript )	{		this( wr);		this.errMessages = new ArrayList();		this.errMessages.add(errMessage);		this.javascript = javascript;	}	public HoException( HoResource wr, ArrayList errMessages, String javascript )	{		this( wr, errMessages );		this.javascript = javascript;	}	public HoException( HoResource wr, String errMessage, String javascript, String detailMessage )	{		this( wr, errMessage, javascript);		this.detailMessage = detailMessage;	}	public HoException( HoResource wr, ArrayList errMessages, String javascript, String detailMessage )	{		this( wr, errMessages , javascript);		this.detailMessage = detailMessage;	}	public String getCode() {		return this.wr.getCode();	}	public void setCode(String code) {		if(this.wr==null) {			this.wr = new HoResource(code);		} else {			this.wr.setCode(code);		}	}	public String getDetailErrMessage() {		return detailMessage;	}	public void setDetailErrMessage(String detailErrMessage) {		this.detailMessage = detailErrMessage;	}	public void setDetailErrMessage(Exception e) {		StringBuffer detail = new StringBuffer();		for( int i=0 ; i< e.getStackTrace().length ; i++) {			detail.append( e.getStackTrace()[i]);		}		this.detailMessage = detail.toString();	}	/**	 * @return	 * @uml.property  name="javascript"	 */	public String getJavascript() {		return javascript;	}	/**	 * @param javascript	 * @uml.property  name="javascript"	 */	public void setJavascript(String javascript) {		this.javascript = javascript;	}	/**	 * @return	 * @uml.property  name="errMessages"	 */	public ArrayList getErrMessages() {		return errMessages;	}	/**	 * @param errMessages	 * @uml.property  name="errMessages"	 */	public void setErrMessages(ArrayList errMessages) {		this.errMessages = errMessages;	}	public Object [] getErrMessage() {		return errMessages.toArray();	}	public void addErrMessage(String errMessage) {		if( this.errMessages == null ) {			this.errMessages = new ArrayList();		}		this.errMessages.add(errMessage);	}	public String getResource() {		return this.wr.getResource();	}	public void setResourceName(String resource) {		if(this.wr!=null) {			this.wr.setResource(resource);		}	}}