package project.jun.was.servlet;import java.net.InetAddress;import java.net.UnknownHostException;import javax.servlet.ServletRequest;import javax.servlet.ServletResponse;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import javax.servlet.jsp.PageContext;import project.jun.config.HoConfig;import project.jun.was.HoSession;/** * @author  sks */public class HoServlet {	private ServletRequest  request;	private ServletResponse response;	private HoConfig        hoConfig;	private HoServlet( PageContext context )	{		this.request = context.getRequest();		this.response = context.getResponse();	}	public HoServlet( PageContext context, HoConfig hoConfig )	{		this(context);		this.hoConfig = hoConfig;	}	public HoServlet( HttpServletRequest request, HttpServletResponse response )	{		this.request = request;		this.response = response;	}	public HoServlet( HttpServletRequest request, HttpServletResponse response, HoConfig hoConfig )	{		this(request, response);		this.hoConfig = hoConfig;	}	/**	 * @return	 * @uml.property  name="request"	 */	public HttpServletRequest getRequest() {		return (HttpServletRequest) this.request;	}	/**	 * @return	 * @uml.property  name="response"	 */	public HttpServletResponse getResponse() {		return (HttpServletResponse) this.response;	}	/**	 * HttpSession을 return한다.	 * @return	 */	public HttpSession getSession() {		return getRequest().getSession();	}	/**	 * HttpSession을 return한다.	 * @return	 */	public HttpSession getSession(boolean create) {		return getRequest().getSession(create);	}	public HoSession getHoSession() {		return new HoSession(getSession(), hoConfig);	}	public HoSession getHoSession(boolean create) {		return new HoSession(getSession(create), hoConfig);	}		/**	 * 서버 IP조회	 * @return	 */	public String getServerIp() {		InetAddress inet = null;		String address = "";		try {			inet = InetAddress.getLocalHost();			address = inet.getHostAddress();		} catch (UnknownHostException e) {					}				return address;	}	/**	 * Client IP조회	 * @return	 */	public String getClientIp() {		return getRequest().getRemoteAddr();	}}