package com.base.system.action;import org.springframework.web.servlet.ModelAndView;import project.jun.was.HoModel;import project.jun.was.servlet.HoServlet;import project.jun.was.parameter.HoParameter;import com.base.system.delegate.LayoutDelegate;public class LayoutAction extends ProjectAction {	public Object execute(String actionFlag, HoModel model, HoParameter parameter, HoServlet   hoServlet) throws Exception {		if(actionFlag.equals("r_layout_left_menu") ) {			LayoutDelegate delegete = (LayoutDelegate) super.getHoDelegate();			delegete.selectMenuList(actionFlag, model, parameter, parameter.getHoConfig());			this.setForwardPage( "/jsp/common/outline/Outline.layout_left_menu.jsp" );		} else if(actionFlag.equals("r_layout_top_menu") ) {			LayoutDelegate delegete = (LayoutDelegate) super.getHoDelegate();			delegete.selectMenuList(actionFlag, model, parameter, parameter.getHoConfig());			this.setForwardPage( "/jsp/common/outline/Outline.layout_top_menu.jsp" );		} else if(actionFlag.equals("r_main_frame") ) {			LayoutDelegate delegete = (LayoutDelegate) super.getHoDelegate();			delegete.selectMenuInfo(actionFlag, model, parameter, parameter.getHoConfig());			this.setForwardPage( "/jsp/common/outline/Outline.main_frame.jsp" );		}		return null;	}}